mapper.select:
SELECT PR."idProject" ,PR."nameProject" ,PR."nameProjectFull" ,PR."descriptionProject" ,PR."dateBeginProject" ,PR."dateEndProject" ,PR."date_start" ,PR."nameAgreement" ,PR."cost" ,PR."costCurrency" ,PR."idCountry" ,PR."idManager" ,PR."idCEO" ,PR."main_worker" ,U1."full_name" as manager ,U2."full_name" as ceo ,U3."full_name" as worker ,TP."nameAgreement" as typeProjectName ,COUNTRIES."nameCountry"
FROM public.tbl_projects PR left outer join public.user U1 on PR."idManager" = U1."id" left outer join public.user U2 on PR."idCEO" = U2."id" left outer join public.user U3 on PR."main_worker" = U3."id" left outer join public.tbl_agreements TP on PR."nameAgreement"=TP."id" left outer join public.tbl_countries COUNTRIES on PR."idCountry"=COUNTRIES."idCountry"
PR."skim" = true
and PR."idProject" = #{idProject}
and PR."dateEndProject" >= #{date}
-- PR."idType" != 3

WITH RECURSIVE r AS ( /* -- стартовая часть рекурсии (т.н. "anchor") */ SELECT id,parent_id, child_id, level FROM public.tbl_projects_subs TPS WHERE parent_id = #{idProject}
UNION /* -- рекурсивная часть */ SELECT TPS.id,TPS.parent_id, TPS.child_id, TPS.level FROM public.tbl_projects_subs TPS JOIN r ON TPS.parent_id = r.child_id )
SELECT id,parent_id, child_id, level FROM r;

SELECT KIP.confirmed ,KIP.project_id as idProject ,KI."id" as idIndicator ,KI."type" as typeIndicator ,KI."name" as nameIndicator ,IV."id" as idIndicatorValue ,IV."year" ,IV."type" as periodType ,IV."type_value" as periodValue ,IV."plan" as planValue ,IV."real" as realValue ,IV."prognosis" FROM public.tbl_key_indicators_projects KIP left outer join public.tbl_key_indicators KI on KIP."indicator_id" = KI."id" and KIP.confirmed = true and KIP.project_id= #{idProject} left outer join public.tbl_key_indicators_values IV on IV."indicator_id" = KI."id" where IV."project_id" = #{idProject}
and IV."year" = #{year}
and IV."year"=date_part('year',CURRENT_DATE)

SELECT KIP.confirmed ,KIP.project_id ,KI."id" as idIndicator ,KI."type" as typeIndicator ,KI."name" as nameIndicator ,IV."id" as idIndicatorValue ,IV."year" ,IV."type" as periodType ,IV."type_value" as periodValue ,IV."plan" as planValue ,IV."real" as realValue ,IV."prognosis" FROM public.tbl_key_indicators_projects KIP left outer join public.tbl_key_indicators KI on KIP."indicator_id" = KI."id" and KIP.confirmed = true left outer join public.tbl_key_indicators_values IV on IV."indicator_id" = KI."id"
IV."project_id" = #{idProject}
and IV."year" = #{year}
